function FollowMode () {
    if (mbit_Robot.Ultrasonic_Car() < 15) {
        mbit_Robot.CarCtrlSpeed(mbit_Robot.CarState.Car_Run, 100)
    } else {
        mbit_Robot.CarCtrl(mbit_Robot.CarState.Car_Stop)
    }
}
