    } else if (uartData == "C") {
        mbit_Robot.CarCtrlSpeed(mbit_Robot.CarState.Car_Left, 110)
    } else if (uartData == "D") {
        mbit_Robot.CarCtrlSpeed(mbit_Robot.CarState.Car_Right, 110)
