function ModeSelect () {
    if (uartData == "S") {
        g_mode = 1
    } else if (uartData == "T") {
        g_mode = 2
    } else if (uartData == "U") {
        g_mode = 3
    } else if (uartData == "V") {
        g_mode = 0
        basic.showLeds(`
