            # . . . #
            . # # # .
            `)
        mbit_Robot.CarCtrl(mbit_Robot.CarState.Car_Stop)
    }
}
function AvoidMode () {
    U_ago = mbit_Robot.Ultrasonic_Car()
    U_now = mbit_Robot.Ultrasonic_Car()
    if (U_ago - U_now <= 10 && U_ago - U_now >= 0 || U_now - U_ago <= 10 && U_now - U_ago >= 0) {
        U_distance = Math.idiv(U_ago + U_now, 2)
        if (U_distance < 15 && U_distance >= 2) {
            mbit_Robot.CarCtrlSpeed(mbit_Robot.CarState.Car_SpinRight, 100)
            basic.pause(300)
        } else {
            mbit_Robot.CarCtrlSpeed(mbit_Robot.CarState.Car_Run, 110)
        }
    }
}
